"""
Provide constant for command line interface.
"""
from enum import (
    Enum,
    auto,
)

SITE_PACKAGES_FOLDER_NAME = 'pip3-multiple-versions-site-packages'

SUCCESSFUL_EXIT_CODE = 0
FAILED_EXIT_CODE = 1


class PackageSourceType(Enum):
    """
    Package source type enum implementation.

    Package can be either folder or single file. If it is package folder, it is required to has `__init__.py` inside.
    """

    file = auto()
    folder = auto()

