"""
Provide implementation of package.
"""
import sys

from multiple_versions.services.site_packages import SitePackages


class Package:
    """
    Package implementation.
    """

    def __init__(self, name: str, version: str, site_packages: SitePackages):
        """
        Construct the object.

        Arguments:
            name (str): name of a package.
            version (str): version of a package.
        """
        self.name = name
        self.version = version
        self.site_packages = site_packages

    @property
    def name_for_folder(self):
        """
        Get name for the folder package will be installed to.

        Folder name is supposed to be human readable.

        Returns:
            Name and version for the folder package as string.
        """
        return self.name + '-' + self.version

    @property
    def name_for_pip3(self):
        """
        Get name as needed for pip3 installation command.

        Returns:
            Name and version for pip3  installation command as string.
        """
        return self.name + '==' + self.version

    def add_to_sys_path(self):
        """
        Add package path to system path to be able to be imported from Python code.

        Returns:
            None.
        """
        sys.path.append(self.site_packages.path + self.name_for_folder)
