"""
Provide implementation of site packages.
"""
import os
import site

from multiple_versions.constants import SITE_PACKAGES_FOLDER_NAME


class SitePackages:
    """
    Site packages implementation.
    """

    def create(self) -> None:
        """
        Create separated site packages folder to collect packages with different versions.

        Returns:
            None.
        """
        if not os.path.exists(self.path):
            os.mkdir(self.path)
            return None

        return None

    @property
    def path(self):
        """
        Get site packages folder path.

        Returns:
            Path to site packages folder path as string.
        """
        installed_packages_folder_path = site.getsitepackages()[0]
        return f'{installed_packages_folder_path}/{SITE_PACKAGES_FOLDER_NAME}'
