"""
Provide implementation of system paths.
"""
import sys
from typing import List


class SysPath:
    """
    System path implementation.
    """

    @staticmethod
    def get() -> List[str]:
        """
        Get system paths.
        """
        return sys.path

    @staticmethod
    def append(path: str) -> None:
        """
        Append path to system paths.

        Arguments:
            path (str): path name.
        """
        sys.path.append(path)
        return None

    @staticmethod
    def delete(path) -> None:
        """
        Remove path from the system paths.

        Arguments:
            path (str): path name.
        """
        sys.path.remove(path)
        return None
